package socket.nio;import lombok.extern.slf4j.Slf4j;import java.nio.CharBuffer;@Slf4jpublic class BufferFillDrain {    private static int index = 0;    private static String[] strings = {            "A random string value",            "The product of an infinite number of monkeys",            "Hey hey we're th Monkees",            "Opening act for the monkees:Jimi Hendrix",            "'Scuse me while i kiss this fly",            "Help Me! Help Me1"    };    public static void main(String[] args) {        CharBuffer buffer = CharBuffer.allocate(100);        while (fillBuffer(buffer)) {            buffer.flip();            drainBuffer(buffer);            buffer.clear();        }    }    private static void drainBuffer(CharBuffer charBuffer) {        while (charBuffer.hasRemaining()) {            log.info("获取缓存队列内容：{}", charBuffer.get());        }        log.info("");    }    private static boolean fillBuffer(CharBuffer buffer) {        if (index >= strings.length) {            return false;        }        String string = strings[index++];        for (int i = 0; i < string.length(); i++) {            buffer.put(string.charAt(i));        }        return true;    }}