package threads.eruptsimultaneously;import java.util.concurrent.locks.Lock;public class TwinsLockTest {    static final Lock lock = new TwinsLock();    public static void main(String[] args) {        for (int i = 0; i < 10; i++) {            Worker w = new Worker();            w.setDaemon(true);            w.start();        }        for (int i = 0; i < 10; i++) {            ThreadStatus.SleepUtils.second(1);            System.out.println();        }    }    static class Worker extends Thread {        @Override        public void run() {            while (true) {                lock.lock();                try {                    ThreadStatus.SleepUtils.second(1);                    System.out.println(Thread.currentThread().getName());                    ThreadStatus.SleepUtils.second(1);                } finally {                    lock.unlock();                }            }        }    }}