package threads.product_comsumer;public class C {    private String lock;    public C(String lock) {        this.lock = lock;    }    public void getValue() {        try {            synchronized (lock) {                System.out.println("C value:" + ValueObject.value);                if (ValueObject.value.equals("")) {                    System.out.println("等待生产者产生消息");                    lock.wait();                }                System.out.println("get value:" + ValueObject.value);                ValueObject.value = "";                lock.notify();            }        } catch (InterruptedException e) {            e.printStackTrace();        }    }}