package threads.tasktime;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;import java.util.Timer;import java.util.TimerTask;public class TimeTest1 {    private static Timer timer = new Timer();    static public class MyTask extends TimerTask {        @Override        public void run() {            System.out.println("运行了！时间为： " + new Date());        }    }    public static void main(String[] args) throws ParseException {        MyTask myTask = new MyTask();        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");        String dateString = "2022-10-25 14:09:00";        Date date = dateFormat.parse(dateString);        System.out.println("字符串时间：" + date.toLocaleString() + " 当前时间：" + new Date().toLocaleString());        timer.schedule(myTask, date, 4000);    }}